import { HTMLAttributes, ReactNode } from 'react';

export type ResizeDirection = "column" | "row";
export type ResizerProps = {
    handleResize: (e: MouseEvent) => void;
    setIsResizing: (val: boolean) => void;
    isResizing: boolean;
    direction?: ResizeDirection;
    className?: string;
    children?: ReactNode;
    style?: React.CSSProperties;
} & Omit<HTMLAttributes<HTMLDivElement>, "onMouseDown" | "onClick">;
export default function Resizer({ handleResize, isResizing, setIsResizing, direction, className, children, style, ...props }: ResizerProps): import("react/jsx-runtime").JSX.Element;
